/**
 * The grouping settings of the Grid component
 */
export interface GroupableSettings {
    /**
     * Determine if grouping by dragging and droping the column headers is allowed.
     */
    enabled: boolean;
    /**
     * Text shown when the grouping area is empty.
     * Default value is "If set to true the user could group the grid by dragging the column header cells. By default grouping is disabled."
     */
    emptyText?: string;
    /**
     * Determines if group footer template should be visible when group is collapsed. Default value is false.
     */
    showFooter: boolean;
}
